import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertMenuItemSchema } from "@shared/schema";
import { z } from "zod";
import { randomBytes } from "crypto";

export async function registerRoutes(app: Express): Promise<Server> {
  if (!process.env.SQUARE_APPLICATION_SECRET) {
    console.error(
      "CRITICAL: SQUARE_APPLICATION_SECRET environment variable is not set!",
    );
    console.error("Square OAuth will not work without this secret.");
  }

  // Generate OAuth state for CSRF protection
  app.get("/api/square/oauth/start", async (req, res) => {
    console.log("[Square OAuth] Generating state...");

    const state = randomBytes(32).toString("hex");

    console.log("[Square OAuth] State:", state);

    res.json({
      state: state,
      baseURL: "https://connect.squareup.com/",
      appId: "sq0idp-o0gFxi0LCTcztITa6DWf2g",
    });
  });

  // Square OAuth callback
  app.get("/api/square/oauth/callback", async (req, res) => {
    try {
      const { code, error, error_description } = req.query;

      console.log("[Square OAuth] Callback received");

      // Handle Square authorization denial
      if (error) {
        console.error(
          "[Square OAuth] Error from Square:",
          error_description || error,
        );
        return res
          .status(400)
          .send(`Authorization failed: ${error_description || error}`);
      }

      if (!code) {
        console.error("[Square OAuth] Missing authorization code");
        return res.status(400).send("Missing authorization code");
      }

      console.log("[Square OAuth] Exchanging code for token...");

      // Exchange authorization code for access token
      const tokenResponse = await fetch(
        "https://connect.squareup.com/oauth2/token",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            client_id: "sq0idp-o0gFxi0LCTcztITa6DWf2g",
            client_secret: process.env.SQUARE_APPLICATION_SECRET,
            code,
            grant_type: "authorization_code",
            redirect_uri: "https://pool-cafe-manager-TalhaNadeem001.replit.app/api/square/oauth/callback",
          }),
        },
      );

      const tokenData = await tokenResponse.json();

      console.log(
        "[Square OAuth] Full token response:",
        JSON.stringify(tokenData, null, 2),
      );

      if (tokenData.error) {
        console.error("[Square OAuth] Error exchanging code:", tokenData);
        return res.status(400).json(tokenData);
      }

      console.log("[Square OAuth] Tokens received successfully");
      console.log(
        "[Square OAuth] Access token:",
        tokenData.access_token ? "present" : "MISSING",
      );
      console.log("[Square OAuth] Merchant ID:", tokenData.merchant_id);

      // Validate all required fields exist
      if (!tokenData.access_token || !tokenData.merchant_id) {
        console.error("[Square OAuth] ERROR: Missing required fields!");
        console.error("[Square OAuth] access_token:", tokenData.access_token);
        console.error("[Square OAuth] merchant_id:", tokenData.merchant_id);
        return res.status(500).json({
          error: "Invalid token response from Square",
          tokenData: tokenData,
        });
      }

      // Save tokens to database
      await storage.saveSquareToken({
        accessToken: tokenData.access_token,
        refreshToken: tokenData.refresh_token || null,
        expiresAt: tokenData.expires_at ? new Date(tokenData.expires_at) : null,
        merchantId: tokenData.merchant_id,
      });

      console.log("[Square OAuth] Tokens saved to database");

      // Redirect user to success page
      res.send(`
        <!DOCTYPE html>
        <html>
          <head><title>Square Connected</title></head>
          <body style="font-family: sans-serif; text-align: center; padding-top: 50px;">
            <h2> Square account connected successfully!</h2>
            <script>
              window.location.href = '/?square_connected=true';
            </script>
          </body>
        </html>
      `);
    } catch (error) {
      console.error("[Square OAuth] Callback error:", error);

      // Return the error structure for debugging
      if (error && typeof error === "object") {
        return res.status(500).json({
          error: "OAuth callback failed",
          details: error,
          message: error instanceof Error ? error.message : "Unknown error",
        });
      }

      res.status(500).send("Server error during OAuth callback");
    }
  });

  // GET /api/square/status - Check Square connection status
  app.get("/api/square/status", async (req, res) => {
    try {
      const token = await storage.getSquareToken();
      res.json({
        connected: !!token,
        merchantId: token?.merchantId || null,
      });
    } catch (error) {
      console.error("Error checking Square status:", error);
      res.status(500).json({ error: "Failed to check Square status" });
    }
  });

  // DELETE /api/square/disconnect - Disconnect Square account
  app.delete("/api/square/disconnect", async (req, res) => {
    try {
      await storage.deleteSquareToken();
      console.log("[Square] Account disconnected");
      res.json({ success: true, message: "Square account disconnected" });
    } catch (error) {
      console.error("Error disconnecting Square:", error);
      res.status(500).json({ error: "Failed to disconnect Square account" });
    }
  });

  // GET /api/square/locations - Fetch Square locations
  app.get("/api/square/locations", async (req, res) => {
    try {
      const token = await storage.getSquareToken();

      if (!token) {
        return res.status(401).json({ error: "Square not connected" });
      }

      console.log("[Square Locations] Fetching locations...");

      const response = await fetch(
        "https://connect.squareup.com/v2/locations",
        {
          method: "GET",
          headers: {
            "Square-Version": "2024-09-19",
            "Authorization": `Bearer ${token.accessToken}`,
            "Content-Type": "application/json",
          },
        }
      );

      if (!response.ok) {
        const errorData = await response.json();
        console.error("[Square Locations] Error response:", errorData);
        return res.status(response.status).json({ error: "Failed to fetch Square locations", details: errorData });
      }

      const data = await response.json();
      console.log(`[Square Locations] Fetched ${data.locations?.length || 0} locations`);

      res.json(data);
    } catch (error) {
      console.error("[Square Locations] Error:", error);
      res.status(500).json({ error: "Failed to fetch Square locations" });
    }
  });

  // GET /api/square/devices - Fetch paired Square Terminal devices
  app.get("/api/square/devices", async (req, res) => {
    try {
      const token = await storage.getSquareToken();

      if (!token) {
        return res.status(401).json({ error: "Square not connected" });
      }

      console.log("[Square Devices] Fetching devices...");

      const response = await fetch(
        "https://connect.squareup.com/v2/devices/codes",
        {
          method: "GET",
          headers: {
            "Square-Version": "2024-09-19",
            "Authorization": `Bearer ${token.accessToken}`,
            "Content-Type": "application/json",
          },
        }
      );

      if (!response.ok) {
        const errorData = await response.json();
        console.error("[Square Devices] Error response:", errorData);
        return res.status(response.status).json({ error: "Failed to fetch Square devices", details: errorData });
      }

      const data = await response.json();
      console.log(`[Square Devices] Fetched ${data.device_codes?.length || 0} device codes`);

      res.json(data);
    } catch (error) {
      console.error("[Square Devices] Error:", error);
      res.status(500).json({ error: "Failed to fetch Square devices" });
    }
  });

  // POST /api/square/terminals/checkouts - Send payment to Square Terminal reader
  app.post("/api/square/terminals/checkouts", async (req, res) => {
    try {
      const token = await storage.getSquareToken();

      if (!token) {
        return res.status(401).json({ error: "Square not connected" });
      }

      const { deviceId, amount, referenceId, note } = req.body;

      if (!deviceId) {
        return res.status(400).json({ error: "Device ID is required" });
      }

      if (!amount || amount <= 0) {
        return res.status(400).json({ error: "Valid amount is required" });
      }

      console.log("[Square Terminal] Creating terminal checkout...", {
        deviceId,
        amount,
        referenceId
      });

      // Generate unique idempotency key
      const idempotencyKey = randomBytes(32).toString("hex");

      const checkoutData = {
        idempotency_key: idempotencyKey,
        checkout: {
          amount_money: {
            amount: Math.round(amount * 100), // Convert to cents
            currency: "USD"
          },
          device_options: {
            device_id: deviceId,
            skip_receipt_screen: false,
            tip_settings: {
              allow_tipping: true
            }
          },
          reference_id: referenceId,
          note: note
        }
      };

      console.log("[Square Terminal] Request data:", JSON.stringify(checkoutData, null, 2));

      const response = await fetch(
        "https://connect.squareup.com/v2/terminals/checkouts",
        {
          method: "POST",
          headers: {
            "Square-Version": "2024-09-19",
            "Authorization": `Bearer ${token.accessToken}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify(checkoutData)
        }
      );

      const data = await response.json();

      if (!response.ok) {
        console.error("[Square Terminal] Error creating checkout:", data);
        return res.status(response.status).json({ 
          error: "Failed to create terminal checkout", 
          details: data 
        });
      }

      console.log("[Square Terminal] Checkout created:", data.checkout?.id, "Status:", data.checkout?.status);

      res.json(data);
    } catch (error) {
      console.error("[Square Terminal] Error:", error);
      res.status(500).json({ error: "Failed to create terminal checkout" });
    }
  });

  // GET /api/square/catalog/items - Fetch menu items from Square
  app.get("/api/square/catalog/items", async (req, res) => {
    try {
      const token = await storage.getSquareToken();

      if (!token) {
        return res.status(401).json({ error: "Square not connected" });
      }

      console.log("[Square Catalog] Fetching items...");

      const response = await fetch(
        "https://connect.squareup.com/v2/catalog/list?types=ITEM,CATEGORY",
        {
          method: "GET",
          headers: {
            "Square-Version": "2024-09-19",
            "Authorization": `Bearer ${token.accessToken}`,
            "Content-Type": "application/json",
          },
        }
      );

      if (!response.ok) {
        const errorData = await response.json();
        console.error("[Square Catalog] Error response:", errorData);
        return res.status(response.status).json({ error: "Failed to fetch Square catalog", details: errorData });
      }

      const data = await response.json();

      // Filter to only include ITEM and CATEGORY types
      if (data.objects) {
        data.objects = data.objects.filter((obj: any) => 
          obj.type === "ITEM" || obj.type === "CATEGORY"
        );
      }

      console.log(`[Square Catalog] Fetched ${data.objects?.length || 0} items and categories`);

      res.json(data);
    } catch (error) {
      console.error("[Square Catalog] Error:", error);
      res.status(500).json({ error: "Failed to fetch Square catalog items" });
    }
  });

  // Menu Items API Routes

  // GET /api/menu-items - Get all menu items
  app.get("/api/menu-items", async (req, res) => {
    try {
      const items = await storage.getAllMenuItems();
      res.json(items);
    } catch (error) {
      console.error("Error fetching menu items:", error);
      res.status(500).json({ error: "Failed to fetch menu items" });
    }
  });

  // POST /api/menu-items - Create a new menu item
  app.post("/api/menu-items", async (req, res) => {
    try {
      const validatedData = insertMenuItemSchema.parse(req.body);
      const item = await storage.createMenuItem(validatedData);
      res.status(201).json(item);
    } catch (error) {
      console.error("Error creating menu item:", error);
      res.status(400).json({ error: "Invalid menu item data" });
    }
  });

  // PATCH /api/menu-items/:id - Update a menu item
  app.patch("/api/menu-items/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const validatedData = insertMenuItemSchema.parse(req.body);
      const item = await storage.updateMenuItem(id, validatedData);
      if (!item) {
        return res.status(404).json({ error: "Menu item not found" });
      }
      res.json(item);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      console.error("Error updating menu item:", error);
      res.status(500).json({ error: "Failed to update menu item" });
    }
  });

  // DELETE /api/menu-items/:id - Delete a menu item
  app.delete("/api/menu-items/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const deleted = await storage.deleteMenuItem(id);
      if (!deleted) {
        return res.status(404).json({ error: "Menu item not found" });
      }
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting menu item:", error);
      res.status(500).json({ error: "Failed to delete menu item" });
    }
  });

  // DELETE /api/menu-items - Clear all menu items
  app.delete("/api/menu-items", async (req, res) => {
    try {
      await storage.clearAllMenuItems();
      res.status(204).send();
    } catch (error) {
      console.error("Error clearing menu items:", error);
      res.status(500).json({ error: "Failed to clear menu items" });
    }
  });

  const httpServer = createServer(app);

  return httpServer;
}

client/src/components/StartSessionDialog.content:
import { useState } from "react";
import { Clock } from "lucide-react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";

interface StartSessionDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  stationName: string;
  onConfirmStart: (customStartTime: number) => void;
}

export function StartSessionDialog({
  open,
  onOpenChange,
  stationName,
  onConfirmStart,
}: StartSessionDialogProps) {
  const now = new Date();
  const defaultTime = now.toTimeString().slice(0, 5); // HH:MM format
  const [customTime, setCustomTime] = useState(defaultTime);

  const handleConfirm = () => {
    const [hours, minutes] = customTime.split(':').map(Number);
    const startDate = new Date();
    startDate.setHours(hours, minutes, 0, 0);

    // If the time is in the future, assume it was yesterday
    if (startDate > now) {
      startDate.setDate(startDate.getDate() - 1);
    }

    onConfirmStart(startDate.getTime());
    onOpenChange(false);
    setCustomTime(defaultTime); // Reset for next time
  };

  const handleUseCurrentTime = () => {
    onConfirmStart(Date.now());
    onOpenChange(false);
    setCustomTime(defaultTime);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-md" data-testid="dialog-start-session">
        <DialogHeader>
          <DialogTitle className="text-2xl" data-testid="text-start-session-title">
            Start Session - {stationName}
          </DialogTitle>
          <DialogDescription>
            Set the start time for this session
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4 py-4">
          <div className="space-y-2">
            <Label htmlFor="start-time" className="flex items-center gap-2">
              <Clock className="w-4 h-4" />
              Start Time
            </Label>
            <Input
              id="start-time"
              type="time"
              value={customTime}
              onChange={(e) => setCustomTime(e.target.value)}
              data-testid="input-start-time"
              className="font-mono text-lg"
            />
            <p className="text-xs text-muted-foreground">
              Current time: {defaultTime}
            </p>
          </div>
        </div>

        <DialogFooter className="flex-col sm:flex-row gap-2">
          <Button
            variant="outline"
            onClick={handleUseCurrentTime}
            data-testid="button-use-current-time"
            className="w-full sm:w-auto"
          >
            Use Current Time
          </Button>
          <Button
            onClick={handleConfirm}
            data-testid="button-confirm-start"
            className="w-full sm:w-auto"
          >
            Start Session
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

client/src/pages/menu-management.tsx.content:
import { useState, useEffect } from "react";
import { Plus, Edit, Trash2, ArrowLeft, RefreshCw, Download } from "lucide-react";
import { useLocation } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import type { MenuItem } from "@shared/schema";

const CATEGORIES = [
  "Lattes",
  "Tea",
  "Refreshers",
  "Slushies",
  "Dessert",
  "Food",
  "Snacks",
  "Custom",
];

export default function MenuManagement() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [dialogOpen, setDialogOpen] = useState(false);
  const [clearAllDialogOpen, setClearAllDialogOpen] = useState(false);
  const [squareItemsDialogOpen, setSquareItemsDialogOpen] = useState(false);
  const [selectedSquareItems, setSelectedSquareItems] = useState<Set<string>>(new Set());
  const [editingItem, setEditingItem] = useState<MenuItem | null>(null);
  const [formData, setFormData] = useState({
    name: "",
    price: "",
    category: "Custom",
  });

  const { data: menuItems = [], isLoading, error } = useQuery<MenuItem[]>({
    queryKey: ["/api/menu-items"],
    retry: 3,
    retryDelay: 1000,
    staleTime: 0, // Always fetch fresh data
    refetchOnMount: 'always', // Always refetch when component mounts
  });

  const { data: squareStatus } = useQuery<{ connected: boolean; merchantId: string | null }>({
    queryKey: ["/api/square/status"],
  });

  const { data: squareCatalog, isLoading: squareCatalogLoading, refetch: refetchSquareCatalog } = useQuery<any>({
    queryKey: ["/api/square/catalog/items"],
    enabled: false,
  });

  useEffect(() => {
    if (error) {
      toast({
        title: "Error Loading Menu",
        description: "Unable to load menu items. Please refresh the page.",
        variant: "destructive",
      });
    }
  }, [error, toast]);

  const createMutation = useMutation({
    mutationFn: async (data: { name: string; price: string; category: string }) => {
      return await apiRequest("POST", "/api/menu-items", {
        name: data.name,
        price: data.price,
        category: data.category,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/menu-items"] });
      toast({
        title: "Item Added",
        description: `${formData.name} has been added to the menu`,
      });
      setDialogOpen(false);
      setFormData({ name: "", price: "", category: "Custom" });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to add menu item",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: { name: string; price: string; category: string } }) => {
      return await apiRequest("PATCH", `/api/menu-items/${id}`, {
        name: data.name,
        price: data.price,
        category: data.category,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/menu-items"] });
      toast({
        title: "Item Updated",
        description: `${formData.name} has been updated`,
      });
      setDialogOpen(false);
      setFormData({ name: "", price: "", category: "Custom" });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update menu item",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("DELETE", `/api/menu-items/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/menu-items"] });
      toast({
        title: "Item Deleted",
        description: "Menu item has been successfully deleted",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete menu item",
        variant: "destructive",
      });
    },
  });

  const clearAllMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest("DELETE", "/api/menu-items");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/menu-items"] });
      toast({
        title: "All Items Cleared",
        description: "All menu items have been deleted",
      });
      setClearAllDialogOpen(false);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to clear menu items",
        variant: "destructive",
      });
    },
  });

  const importSquareItemsMutation = useMutation({
    mutationFn: async (items: { name: string; price: string; category: string }[]) => {
      const promises = items.map(item => apiRequest("POST", "/api/menu-items", item));
      return await Promise.all(promises);
    },
    onSuccess: (_, items) => {
      queryClient.invalidateQueries({ queryKey: ["/api/menu-items"] });
      toast({
        title: "Items Imported",
        description: `Successfully imported ${items.length} item${items.length > 1 ? 's' : ''} from Square`,
      });
      setSquareItemsDialogOpen(false);
      setSelectedSquareItems(new Set());
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to import items from Square",
        variant: "destructive",
      });
    },
  });

  const handleOpenAddDialog = () => {
    setEditingItem(null);
    setFormData({ name: "", price: "", category: "Custom" });
    setDialogOpen(true);
  };

  const handleOpenEditDialog = (item: MenuItem) => {
    setEditingItem(item);
    const priceValue = typeof item.price === 'string' ? parseFloat(item.price) : item.price;
    setFormData({
      name: item.name,
      price: priceValue.toFixed(2),
      category: item.category,
    });
    setDialogOpen(true);
  };

  const handleSaveMenuItem = () => {
    const price = parseFloat(formData.price);
    if (!formData.name.trim() || isNaN(price) || price <= 0) {
      toast({
        title: "Invalid Input",
        description: "Please enter a valid name and price",
        variant: "destructive",
      });
      return;
    }

    if (editingItem) {
      updateMutation.mutate({
        id: editingItem.id,
        data: formData,
      });
    } else {
      createMutation.mutate(formData);
    }
  };

  const handleDeleteItem = (item: MenuItem) => {
    deleteMutation.mutate(item.id);
  };

  const handleImportSelectedItems = () => {
    if (!squareCatalog?.objects || selectedSquareItems.size === 0) {
      toast({
        title: "No Items Selected",
        description: "Please select at least one item to import",
        variant: "destructive",
      });
      return;
    }

    const itemsToImport: { name: string; price: string; category: string }[] = [];

    // Build category lookup map
    const categories = squareCatalog.objects.filter((obj: any) => obj.type === "CATEGORY");
    const categoryMap = new Map();
    categories.forEach((cat: any) => {
      categoryMap.set(cat.id, cat.category_data?.name || "Custom");
    });

    squareCatalog.objects.forEach((item: any) => {
      if (item.type === "ITEM" && item.item_data.variations) {
        item.item_data.variations.forEach((variation: any) => {
          const variationKey = `${item.id}-${variation.id}`;
          if (selectedSquareItems.has(variationKey)) {
            const price = variation.item_variation_data.price_money?.amount || 0;
            const variationName = variation.item_variation_data.name;
            const itemName = variationName === "Regular" 
              ? item.item_data.name 
              : `${item.item_data.name} - ${variationName}`;

            // Get category name from reporting_category (primary category)
            const categoryId = item.item_data.reporting_category?.id;
            const categoryName = categoryId 
              ? categoryMap.get(categoryId) || "Custom"
              : "Custom";

            itemsToImport.push({
              name: itemName,
              price: (price / 100).toFixed(2),
              category: categoryName,
            });
          }
        });
      }
    });

    importSquareItemsMutation.mutate(itemsToImport);
  };

  const toggleSquareItem = (itemId: string, variationId: string) => {
    const key = `${itemId}-${variationId}`;
    setSelectedSquareItems(prev => {
      const newSet = new Set(prev);
      if (newSet.has(key)) {
        newSet.delete(key);
      } else {
        newSet.add(key);
      }
      return newSet;
    });
  };

  const selectAllSquareItems = () => {
    if (!squareCatalog?.objects) return;

    const allKeys = new Set<string>();
    squareCatalog.objects.forEach((item: any) => {
      if (item.type === "ITEM" && item.item_data?.variations) {
        item.item_data.variations.forEach((variation: any) => {
          allKeys.add(`${item.id}-${variation.id}`);
        });
      }
    });
    setSelectedSquareItems(allKeys);
  };

  const deselectAllSquareItems = () => {
    setSelectedSquareItems(new Set());
  };

  const groupedItems = menuItems.reduce((acc, item) => {
    if (!acc[item.category]) {
      acc[item.category] = [];
    }
    acc[item.category].push(item);
    return acc;
  }, {} as Record<string, MenuItem[]>);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <p className="text-muted-foreground">Loading menu items...</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <header className="border-b sticky top-0 bg-background z-10">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <Button
                variant="ghost"
                size="icon"
                onClick={() => setLocation("/")}
                data-testid="button-back"
              >
                <ArrowLeft className="w-5 h-5" />
              </Button>
              <div>
                <h1 className="text-2xl font-bold" data-testid="text-page-title">
                  Menu Management
                </h1>
                <p className="text-sm text-muted-foreground">
                  Add, edit, and organize menu items
                </p>
              </div>
            </div>
            <div className="flex items-center gap-2">
              {squareStatus?.connected && (
                <Button
                  variant="outline"
                  onClick={() => {
                    setSquareItemsDialogOpen(true);
                    refetchSquareCatalog();
                  }}
                  data-testid="button-sync-square"
                >
                  <RefreshCw className="w-4 h-4 mr-2" />
                  Import from Square
                </Button>
              )}
              {menuItems.length > 0 && (
                <Button
                  variant="destructive"
                  onClick={() => setClearAllDialogOpen(true)}
                  data-testid="button-clear-all"
                >
                  <Trash2 className="w-4 h-4 mr-2" />
                  Clear All Items
                </Button>
              )}
              <Button onClick={handleOpenAddDialog} data-testid="button-add-menu-item">
                <Plus className="w-4 h-4 mr-2" />
                Add Menu Item
              </Button>
            </div>
          </div>
        </div>
      </header>

      <main className="container mx-auto px-4 py-6">
        <div className="max-w-4xl mx-auto space-y-6">
          {Object.keys(groupedItems).length === 0 ? (
            <Card className="p-12 text-center">
              <div className="space-y-4">
                <div className="w-16 h-16 mx-auto rounded-full bg-muted flex items-center justify-center">
                  <Plus className="w-8 h-8 text-muted-foreground" />
                </div>
                <div>
                  <h3 className="text-lg font-semibold mb-2">No Menu Items</h3>
                  <p className="text-muted-foreground mb-4">
                    Get started by adding your first menu item
                  </p>
                  <Button onClick={handleOpenAddDialog} data-testid="button-add-first-item">
                    <Plus className="w-4 h-4 mr-2" />
                    Add Menu Item
                  </Button>
                </div>
              </div>
            </Card>
          ) : (
            Object.entries(groupedItems).map(([category, items]) => (
              <Card key={category} className="p-6" data-testid={`category-${category.toLowerCase()}`}>
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-xl font-semibold text-primary">
                    {category}
                  </h2>
                  <span className="text-sm text-muted-foreground">
                    {items.length} {items.length === 1 ? "item" : "items"}
                  </span>
                </div>
                <div className="space-y-3">
                  {items.map((item) => (
                    <div
                      key={item.id}
                      className="flex items-center justify-between p-4 bg-muted/50 rounded-lg"
                      data-testid={`menu-item-${item.id}`}
                    >
                      <div className="flex-1">
                        <p className="font-semibold" data-testid={`text-item-name-${item.id}`}>
                          {item.name}
                        </p>
                        <p className="text-lg font-mono text-green-600 dark:text-green-400" data-testid={`text-item-price-${item.id}`}>
                          ${parseFloat(item.price).toFixed(2)}
                        </p>
                      </div>
                      <div className="flex gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleOpenEditDialog(item)}
                          data-testid={`button-edit-${item.id}`}
                        >
                          <Edit className="w-4 h-4 mr-1" />
                          Edit
                        </Button>
                        <Button
                          variant="destructive"
                          size="sm"
                          onClick={() => handleDeleteItem(item)}
                          data-testid={`button-delete-${item.id}`}
                        >
                          <Trash2 className="w-4 h-4 mr-1" />
                          Delete
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              </Card>
            ))
          )}
        </div>
      </main>

      <Dialog open={dialogOpen} onOpenChange={setDialogOpen}>
        <DialogContent data-testid="dialog-menu-item">
          <DialogHeader>
            <DialogTitle data-testid="text-dialog-title">
              {editingItem ? "Edit Menu Item" : "Add Menu Item"}
            </DialogTitle>
            <DialogDescription>
              {editingItem
                ? "Update the details of this menu item"
                : "Add a new item to your menu"}
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="item-name">Item Name</Label>
              <Input
                id="item-name"
                placeholder="e.g., Burger, Fries, Latte"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                data-testid="input-item-name"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="item-price">Price</Label>
              <Input
                id="item-price"
                type="number"
                step="0.01"
                min="0"
                placeholder="0.00"
                value={formData.price}
                onChange={(e) => setFormData({ ...formData, price: e.target.value })}
                data-testid="input-item-price"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="item-category">Category</Label>
              <Select
                value={formData.category}
                onValueChange={(value) => setFormData({ ...formData, category: value })}
              >
                <SelectTrigger data-testid="select-category">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {CATEGORIES.map((category) => (
                    <SelectItem key={category} value={category}>
                      {category}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>

          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setDialogOpen(false)}
              data-testid="button-cancel"
            >
              Cancel
            </Button>
            <Button
              onClick={handleSaveMenuItem}
              disabled={createMutation.isPending || updateMutation.isPending}
              data-testid="button-save"
            >
              {editingItem ? "Update" : "Add"} Item
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      <AlertDialog open={clearAllDialogOpen} onOpenChange={setClearAllDialogOpen}>
        <AlertDialogContent data-testid="dialog-clear-all-confirm">
          <AlertDialogHeader>
            <AlertDialogTitle>Clear All Menu Items?</AlertDialogTitle>
            <AlertDialogDescription>
              This action cannot be undone. This will permanently delete all {menuItems.length} menu items from your database.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel data-testid="button-cancel-clear">Cancel</AlertDialogCancel>
            <AlertDialogAction
              onClick={() => clearAllMutation.mutate()}
              disabled={clearAllMutation.isPending}
              className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
              data-testid="button-confirm-clear"
            >
              {clearAllMutation.isPending ? "Clearing..." : "Clear All Items"}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>

      <Dialog open={squareItemsDialogOpen} onOpenChange={setSquareItemsDialogOpen}>
        <DialogContent className="max-w-4xl max-h-[85vh]" data-testid="dialog-square-items">
          <DialogHeader>
            <DialogTitle>Import from Square</DialogTitle>
            <DialogDescription>
              Select items to import into your menu
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-4 py-4">
            {squareCatalogLoading ? (
              <div className="text-center py-8">
                <div className="w-12 h-12 border-4 border-primary border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
                <p className="text-muted-foreground">Loading Square menu items...</p>
              </div>
            ) : squareCatalog?.objects && squareCatalog.objects.length > 0 ? (
              <>
                <div className="flex items-center justify-between border-b pb-3">
                  <div className="flex items-center gap-2">
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={selectAllSquareItems}
                      data-testid="button-select-all-square"
                    >
                      Select All
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={deselectAllSquareItems}
                      data-testid="button-deselect-all-square"
                    >
                      Deselect All
                    </Button>
                  </div>
                  <span className="text-sm text-muted-foreground">
                    {selectedSquareItems.size} selected
                  </span>
                </div>
                <div className="space-y-4 max-h-[50vh] overflow-y-auto pr-2">
                  {(() => {
                    // Build category lookup map
                    const categories = squareCatalog.objects.filter((obj: any) => obj.type === "CATEGORY");
                    const categoryMap = new Map();
                    categories.forEach((cat: any) => {
                      categoryMap.set(cat.id, cat.category_data?.name || "Uncategorized");
                    });

                    // Filter only ITEM type objects
                    const items = squareCatalog.objects.filter((obj: any) => obj.type === "ITEM");

                    // Group items by their reporting category
                    const groupedItems = new Map<string, any[]>();
                    items.forEach((item: any) => {
                      const categoryId = item.item_data.reporting_category?.id || "uncategorized";
                      if (!groupedItems.has(categoryId)) {
                        groupedItems.set(categoryId, []);
                      }
                      groupedItems.get(categoryId)!.push(item);
                    });

                    // Render items grouped by category
                    return Array.from(groupedItems.entries()).map(([categoryId, categoryItems]) => {
                      const categoryName = categoryMap.get(categoryId) || "Uncategorized";

                      return (
                        <div key={categoryId} className="space-y-3">
                          <div className="sticky top-0 bg-background z-10 pb-2 border-b">
                            <h3 className="font-bold text-lg">{categoryName}</h3>
                            <p className="text-xs text-muted-foreground">
                              {categoryItems.length} item{categoryItems.length !== 1 ? 's' : ''}
                            </p>
                          </div>

                          <div className="space-y-2 pl-2">
                            {categoryItems.map((item: any) => (
                              <Card key={item.id} className="p-3">
                                <div className="space-y-2">
                                  <h4 className="font-semibold text-sm">{item.item_data.name}</h4>
                                  {item.item_data.variations && item.item_data.variations.length > 0 && (
                                    <div className="space-y-1.5">
                                      {item.item_data.variations.map((variation: any) => {
                                        const variationKey = `${item.id}-${variation.id}`;
                                        const isSelected = selectedSquareItems.has(variationKey);
                                        const price = variation.item_variation_data.price_money?.amount || 0;
                                        const variationName = variation.item_variation_data.name;

                                        return (
                                          <div
                                            key={variation.id}
                                            className="flex items-center justify-between p-2.5 rounded-md border hover-elevate cursor-pointer"
                                            onClick={() => toggleSquareItem(item.id, variation.id)}
                                            data-testid={`square-item-${variationKey}`}
                                          >
                                            <div className="flex items-center gap-3 flex-1">
                                              <Checkbox
                                                checked={isSelected}
                                                onCheckedChange={() => toggleSquareItem(item.id, variation.id)}
                                                data-testid={`checkbox-${variationKey}`}
                                              />
                                              <div className="flex-1">
                                                <p className="font-medium text-sm">{variationName}</p>
                                              </div>
                                            </div>
                                            <span className="font-mono font-bold text-primary">
                                              ${(price / 100).toFixed(2)}
                                            </span>
                                          </div>
                                        );
                                      })}
                                    </div>
                                  )}
                                </div>
                              </Card>
                            ))}
                          </div>
                        </div>
                      );
                    });
                  })()}
                </div>
              </>
            ) : (
              <div className="text-center py-8">
                <p className="text-muted-foreground">No items found in your Square catalog</p>
              </div>
            )}
          </div>

          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setSquareItemsDialogOpen(false)}
              data-testid="button-close-square-dialog"
            >
              Cancel
            </Button>
            <Button
              onClick={handleImportSelectedItems}
              disabled={importSquareItemsMutation.isPending || selectedSquareItems.size === 0}
              data-testid="button-import-selected"
            >
              <Download className="w-4 h-4 mr-2" />
              {importSquareItemsMutation.isPending 
                ? "Importing..." 
                : `Import ${selectedSquareItems.size > 0 ? `(${selectedSquareItems.size})` : ''}`}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

